package org.interviewproblems.play;

import java.util.HashMap;
import java.util.Map;

public class WildcardMatching {

   public boolean isMatch(String s, String p) {
      return isMatch(s, 0, p, 0);
   }

   public boolean isMatch(String s, int sIt ,String p, int pIt) {
      if ( sIt == s.length()) return (pIt == p.length() - 1 && p.charAt(p.length() - 1) ==  '*') || pIt == p.length();
      while (pIt < p.length() && sIt < s.length()) {
         char c = p.charAt(pIt++);
         switch (c) {
            case '*': {
               System.out.println("sIt = " + sIt);
               System.out.println("pIt = " + pIt);
               return decorateIsMatch(s, sIt + 1, p, pIt - 1) || decorateIsMatch(s, sIt, p, pIt);
            }
            case '?': {
               sIt++;
               break;
            }
            default: {
               if (c != s.charAt(sIt++)) return false;
            }
         }
      }
      return pIt == p.length() && sIt == s.length();
   }

   Map<String, Boolean> memo = new HashMap<String, Boolean>();

   public boolean decorateIsMatch(String s, int sIt, String p, int pIt) {
      String key = sIt + "-" + pIt;
      if (memo.containsKey(key))
         return memo.get(key);
      Boolean res = isMatch(s,sIt, p, pIt);
      memo.put(key, res);
      return res;
   }


   public static void main(String[] args) {
      String a = "";
      String b = "*";
      System.out.println(a.length());
      System.out.println(b.length());
      boolean match = new WildcardMatching().isMatch(a, b);
      System.out.println("match = " + match);
   }

}
